package DDosAttack

import (
	"fmt"
	"strings"
	"testing"
)

func TestContractGenerator_ToHex(t *testing.T) {
	//var code = ""
	//var code = ""
	var code = ""
	operations, err := Disasm(code)
	if err != nil {
		t.Errorf("Failed to disasm code")
	}

	contractGenerator := NewContractGenerator()
	for _, op := range operations {
		contractGenerator.AddOperation(op)
	}
	newCode := contractGenerator.ToHex()
	if ret := strings.Compare(code, newCode[2:]); ret != 0 {
		t.Errorf("recover failed")
		fmt.Println(newCode[2:])
		fmt.Println(code)
	}
	contractGenerator.Show()
}
